    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed

        boolean salvarVenda = VendaService.ComputarVenda(Integer.parseInt(txtCod.getText()),
                Integer.parseInt(txtQuant.getText()));
        if (salvarVenda == false) {
            JOptionPane.showMessageDialog(null, "Nao foi possivel computar esta venda");
            return;
        }else{
            
            limpar();
        }
  
        
    }//GEN-LAST:event_btnSalvarActionPerformed

    private void limpar() {
        txtCod.setText("");
        txtQuant.setText("");
        txtCod.requestFocus();       
    }

